// SPDX-License-Identifier: MIT
pragma solidity >=0.4.21 <0.9.0;

contract Voting{

    struct Voter{
        bool auth;
        bool voted;
        uint256 vote;
    }

    struct candidate{
        string name;
        uint256 voteCount;
    }

    mapping(address => Voter) public Voters;
    candidate [] public candidates;
    address public admin;
    constructor() public{
        admin = msg.sender;
    }
    modifier onlyOwner{
        require(msg.sender == admin);
        _;
    }

    function Addvoter(address _addr)external onlyOwner{
        Voters[_addr].auth = true;
    }

    function Addcandidate(string calldata _name) external onlyOwner{
        candidates.push(candidate(_name,0));
    }

    function vote(uint _num) external returns(bool){
        require(Voters[msg.sender].auth);
        require(!Voters[msg.sender].voted);
        candidates[_num].voteCount += 1;
        Voters[msg.sender].voted = true;
        return true;
    }

    function declareWinner()external view onlyOwner returns(string memory,uint256){
        string memory winner = "";
        uint currentVote = 0;
        for(uint i = 0; i < candidates.length; ++i){
            if(candidates[i].voteCount > currentVote){
                winner = candidates[i].name;
                currentVote = candidates[i].voteCount;
            }}
        return (winner,currentVote);

}

}
